    /**
     * 表格数据部分渲染入口
     * @param res
     * @param curr
     * @param count
     * @param sort
     */
    Class.prototype.renderData = function(res, curr, count, sort) {
        var that = this,
            options = that.config,
            data = res[options.response.dataName] || [],
            trs = [],
            trs_fixed = [],
            trs_fixed_r = []
            //渲染视图
            ,
            render = function() { //后续性能提升的重点
                if (!sort && that.sortKey) {
                    return that.sort(that.sortKey.field, that.sortKey.sort, true);
                }

                layui.each(data, function(i1, obj) {
                    var uo = that.treeFindUpData(obj);
                    var display = "";
                    if (!obj[table.config.cols.isShow] && options.isTree) {
                        display = "display: none;";
                    }
                    var tds = [],
                        tds_fixed = [],
                        tds_fixed_r = [],
                        numbers = i1 + options.limit * (curr - 1) + 1; //序号
                    if (obj.length === 0) return;
                    if (!sort) {
                        obj[table.config.indexName] = i1;
                    }
                    var _result = that.renderTr(obj, numbers);
                    tds = _result._tds;
                    tds_fixed = _result._tds_l;
                    tds_fixed_r = _result._tds_r;
                    trs.push('<tr style="' + display + '" data-index="' + i1 + '" ' + that.renderTrUpids(obj) + '>' + tds.join('') +
                        '</tr>');
                    trs_fixed.push('<tr style="' + display + '"  data-index="' + i1 + '">' + tds_fixed.join('') + '</tr>');
                    trs_fixed_r.push('<tr style="' + display + '"  data-index="' + i1 + '">' + tds_fixed_r.join('') + '</tr>');
                });
                //if(data.length === 0) return;
                that.layBody.scrollTop(0);
                that.layMain.find('.' + NONE).remove();
                that.layMain.find('tbody').html(trs.join(''));
                that.layFixLeft.find('tbody').html(trs_fixed.join(''));
                that.layFixRight.find('tbody').html(trs_fixed_r.join(''));
                that.renderForm();
                that.haveInit ? that.scrollPatch() : setTimeout(function() {
                    that.scrollPatch();
                }, 50);
                that.haveInit = true;
                layer.close(that.tipsIndex);
            };
        that.key = options.id || options.index;
        // table.cache[that.key] = data; //记录数据
        table.setDataList(that.key, data);
        //显示隐藏分页栏
        that.layPage[data.length === 0 && curr == 1 ? 'addClass' : 'removeClass'](HIDE);
        //排序
        if (sort) {
            return render();
        }
        if (data.length === 0) {
            that.renderForm();
            that.layFixed.remove();
            that.layMain.find('tbody').html('');
            that.layMain.find('.' + NONE).remove();
            return that.layMain.append('<div class="' + NONE + '">' + (res[options.response.msgName] ? res[options.response.msgName] :
                options.text.none) + '</div>');
        }
        render();
        that.renderPage(count); //分页渲染
        //calss加载完成
        table.pushClassIds(options.id, true);
    };
